#include <iostream>
#include <string>
#include <array>
#include <stdlib.h>


class Entity
{
private:
    std::string m_Name;
    mutable int m_DebugCount = 0;

public:
    const std::string& GetName() const {
        m_DebugCount ++;
        return m_Name;
    }
    void PrintGetNameCount() const
    {
        std::cout << m_DebugCount << std::endl;
    }
};


int main(void)
{
    const Entity e;
    e.GetName();
    std::cout << e.GetName() << std::endl;
    std::cout << e.GetName() << std::endl;
    std::cout << e.GetName() << std::endl;
    std::cout << e.GetName() << std::endl;
    e.PrintGetNameCount();
    std::cout << 'x' << std::endl;


}
