cells.data:
* {
        margin: 0;
        padding: 0;
        /*盒子模型方便计算*/
        box-sizing: border-box;
    }

    html {
        /*全局设定*/
        font-size: 62.5%;
    }

    body {
        background-color: #f5f6f8;
        font-family: 'PingFang SC', Helvetica, sans-serif;
        /*rem单位*/
        font-size: 1.4rem;
    }

    .main {
        margin: 0 auto;
        max-width: 65rem;
        /*background-color: #ddd;*/
        padding-bottom: 30rem;
    }
