cells.data:
[

    {
        "keys": ["tab"],
        "command": "move",
        "args":
        {
            "by": "characters",
            "forward": true
        },
        "context": [
        {
            "key": "following_text",
            "operator": "regex_contains",
            "operand": "^[)\\]\\>\\'\\\"\\ %>\\}\\;\\,]",
            "match_all": true
        },
        {
            "key": "preceding_text",
            "operator": "not_regex_match",
            "operand": "^.*\\{$",
            "match_all": true
        }]
    },
    {
        "keys": ["super+shift+j"],
        "command": "prev_view"
    },
    {
        "keys": ["super+shift+k"],
        "command": "next_view"
    },
    {
        "keys": ["super+l"],
        "command": "move",
        "args":
        {
            "by": "characters",
            "forward": true
        }
    },
    //左
    {
        "keys": ["super+j"],
        "command": "move",
        "args":
        {
            "by": "characters",
            "forward": false
        }
    },
    //上
    {
        "keys": ["super+i"],
        "command": "move",
        "args":
        {
            "by": "lines",
            "forward": false
        }
    },
    //下
    {
        "keys": ["super+k"],
        "command": "move",
        "args":
        {
            "by": "lines",
            "forward": true
        }
    },
    {
        "keys": ["super+9", "super+0"],
        "command": "toggle_side_bar"
    },
    {
        "keys": ["shift+super+l"],
        "command": "expand_selection",
        "args":
        {
            "to": "line"
        }
    },
    {
        "keys": ["super+shift+b"],
        "command": "build",
        "args":
        {
            "select": true
        }
    },
    {
        "keys": ["super+shift+o"],
        "command": "open_in_browser"
    },




    // Chrome
    {
        "keys": ["command+1"],
        "command": "side_bar_files_open_with",
        "args":
        {
            "paths": [],
            "application": "/Applications/Yandex.app",
            "extensions": ".*"
        }
    },
    // 习惯command+b默认Chrome
    {
        "keys": ["super+shift+o"],
        "command": "side_bar_files_open_with",
        "args":
        {
            "paths": [],
            "application": "/Applications/Google Chrome.app",
            "extensions": ".*"
        }
    },

    // Safari
    {
        "keys": ["super+shift+"],
        "command": "side_bar_files_open_with",
        "args":
        {
            "paths": [],
            "application": "/Applications/Safari.app",
            "extensions": ".*"
        }
    },

    // // Firefox
    // { "keys": ["super+shift+u"], "command": "side_bar_files_open_with",
    //      "args": {
    //         "paths": [],
    //         "application": "/Applications/Firefox.app",
    //         "extensions":".*"
    //     }
    // },

    {
        "keys": ["super+h"],
        "command": "move",
        "args":
        {
            "by": "subwords",
            "forward": false
        }
    },
    {
        "keys": ["super+'"],
        "command": "move",
        "args":
        {
            "by": "subword_ends",
            "forward": true
        }
    },
    {
        "keys": ["super+shift+h"],
        "command": "move",
        "args":
        {
            "by": "subwords",
            "forward": false,
            "extend": true
        }
    },
    {
        "keys": ["super+shift+l"],
        "command": "move",
        "args":
        {
            "by": "subword_ends",
            "forward": true,
            "extend": true
        }
    },

    {
        "keys": ["ctrl+alt+up"],
        "command": "scroll_lines",
        "args":
        {
            "amount": 1.0
        }
    },
    {
        "keys": ["super+shift+h"],
        "command": "htmlprettify"
    },
    {
        "keys": ["super+shift+a"],
        "command": "align_tab",

        "args":
        {
            "user_input": ["=/f", "\\?/f", ":/f"]
        }
    },

    {
        "keys": ["super+shift+r"],
        "command": "toggle_setting",
        "args":
        {
            "setting": "word_wrap"
        }
    },
    { "keys": ["super+0"], "command": "focus_side_bar" },






]
